header {
    padding-top: 23px;

    nav {
        padding: 0 24px;
        display: flex;
        justify-content: space-between;
        align-items: center;
    }

    .main__btn {
        @include mainBtn;
    }

    @include mQ(680px) {
        padding-top: 40px;

        nav {
            padding: 0 39px;
        }
    }

    @include mQ(1280px) {
        padding-top: 55px;

        nav {
            padding: 0 165px;
        }
    }
}

.hero {
    .hero__content {
        padding: 0 24px;
        margin-top: 60px;
    }


    .hero__content__title {
        font-size: calculateRem(48);
        line-height: 48px;
        font-weight: 900;
    }

    .hero__content__description {
        @include contentText;
    }

    .hero__content__primary {
        margin-top: 32px;
        display: flex;
        justify-content: space-between;
        align-items: center;
        font-size: calculateRem(16);
    }

    .hero__content__btn {
        background-color: $orange-bg;
        color: $white-tx;
    }

    .hero__content__span {
        display: inline-block;
        font-weight: 600;
        color: $para-tx-color;
    }

    .hero__img__img {
        width: 100%;
        margin-left: 24px;
        margin-top: 67px;
        border-radius: 24px 0 0 24px;
    }

    @include mQ(680px) {
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        margin-top: 65px;
    }

    @include mQ(1280px) {
        padding: 0 165px;
        gap: 140px;

        .hero__content {
            padding: 0;  
            align-self: center;         
        }

        .hero__content__title {
            font-size: calculateRem(56);
            line-height:  56px;
        }

        .hero__content__description {
            font-size: calculateRem(18);
           
        }

        .hero__content__primary {           
            gap: 60px;
            justify-content: flex-start;
        }
    }

}

